<?php	//Came orgionally from the CBORD project file \full\inc\htmlhead.php	$institution_color = '#43801A';	$institution_name = 'CBORD University';		if(isset($_SESSION['header'])){		$screenText = $_SESSION['header'];	}else{		$content = new Content();		$screenText = $content->retrieveStringList($_SESSION['institutionId'], $_SESSION['locale'], "get_web_gui", "header");	}	if(isset($screenText->return->string)){		//** Load the label variables from the list.		foreach($screenText->return->string as $element){			if($element->name == 'institution_color')	$institution_color = $element->value;			if($element->name == 'institution_name')	$institution_name = $element->value;		}	} else {		displayError(NULL, $error_message['localization'], $screenText, NULL);	}		//Make a secondary color 40 Shades lighter for the borders	$r = hexdec(substr($institution_color, 1, 2))+40;	$g = hexdec(substr($institution_color, 3, 2))+40;	$b = hexdec(substr($institution_color, 5, 2))+40;	$border = fixRGB($r, $g, $b);		//Another darker border & Header Font	$r = hexdec(substr($institution_color, 1, 2))-10;	$g = hexdec(substr($institution_color, 3, 2))-10;	$b = hexdec(substr($institution_color, 5, 2))-10;	$borderDark = fixRGB($r, $g, $b);		//Make two colors for gradients	$r = hexdec(substr($institution_color, 1, 2))+52;	$g = hexdec(substr($institution_color, 3, 2))+52;	$b = hexdec(substr($institution_color, 5, 2))+52;	$grad1 = fixRGB($r, $g, $b);		$r = hexdec(substr($institution_color, 1, 2))+26;	$g = hexdec(substr($institution_color, 3, 2))+26;	$b = hexdec(substr($institution_color, 5, 2))+26;	$grad2 = fixRGB($r, $g, $b);		//Make two colors for dark gradients	$r = hexdec(substr($institution_color, 1, 2))-11;	$g = hexdec(substr($institution_color, 3, 2))-20;	$b = hexdec(substr($institution_color, 5, 2))-4;	$darkGrad1 = fixRGB($r, $g, $b);		$r = hexdec(substr($institution_color, 1, 2))-5;	$g = hexdec(substr($institution_color, 3, 2))-8;	$b = hexdec(substr($institution_color, 5, 2))-2;	$darkGrad2 = fixRGB($r, $g, $b);		//Make a color 15 shades lighter for the Right rail header	$r = hexdec(substr($institution_color, 1, 2))+15;	$g = hexdec(substr($institution_color, 3, 2))+15;	$b = hexdec(substr($institution_color, 5, 2))+15;	$rightRail = fixRGB($r, $g, $b);	$rightRail = "rgb( $r , $g , $b )";		//Make a very light color for login input background	$r = hexdec(substr($institution_color, 1, 2))+113;	$g = hexdec(substr($institution_color, 3, 2))+113;	$b = hexdec(substr($institution_color, 5, 2))+113;	$inputField = fixRGB($r, $g, $b);		//Make sure the Hex don't go above FF or below 0 and is 2 digits	function fixRGB($r, $g, $b){						if($r > 255) $r = "FF";		else if($r < 0) $r = "00";		else $r = dechex($r);				if(strlen($r) < 2) $r = "0".$r;				if($g > 255) $g = "FF";		else if($g < 0) $g = "00";		else $g = dechex($g);				if(strlen($g) < 2) $g = "0".$r;				if($b > 255) $b = "FF";		else if($b < 0) $b = "00";		else $b = dechex($b);				if(strlen($b) < 2) $b = "0".$b;				return "#".$r.$g.$b;	}?><!DOCTYPE html PUBLIC "-//W3C//DTD XHTML 1.1//EN" "http://www.w3.org/TR/xhtml11/DTD/xhtml11.dtd"><html xmlns="http://www.w3.org/1999/xhtml"><head>	<meta http-equiv="Content-Type" content="text/html; charset=UTF-8" />	<script type="text/javascript" src="../scripts/jquery-1.4.4.min.js"></script>	<script type="text/javascript" src="scripts/jquery.infieldlabel.min.js"></script>	<script type="text/javascript" src="scripts/jquery.validate.min.js"></script>	<script type="text/javascript" src="scripts/jquery.select_skin.min.js"></script>	<script type="text/javascript" src="scripts/global_full.js"></script>    <!--[if lte IE 8]><script type="text/javascript" src="scripts/global_ie8.js"></script><![endif]-->	<link rel="stylesheet" type="text/css" href="styles/global_full.css" />	<link rel="stylesheet" type="text/css" href="styles/global_print.css" media="print" />	<!--[if lte IE 8]><link rel="sty